INSERT INTO ournextrace.races VALUES (30, 'Masters SCY Valentine''s Meet(Saturday)', '2019-02-09 00:00:00', 'This meet will be using 8 of the facility’s 20 short course yard lanes with Daktronics timing for competition and 10 lanes will be provided for warm-ups during the course of the meet. All events will be swum in numerical order slow to fast. ', 32, 'https://www.clubassistant.com/club/meet_information.cfm?c=1855&smid=11262', true, '', 0, '2019-10-06 18:00:00', true);
INSERT INTO ournextrace.races VALUES (31, 'St. Petersburg Distance Classic', '2019-02-10 00:00:00', 'With four competitive timed races from 3.1 to 26.2 miles, we have something for everyone during a winter weekend on Florida''s beautiful gulf coast, and so we invite athletes of all abilities to join us at the St. Petersburg Distance Classic.', 33, 'https://www.active.com/st-petersburg-fl/running/distance-running-races/inaugural-st-petersburg-distance-classic-2019', true, '', 0, '2019-09-07 22:21:11', true);
INSERT INTO ournextrace.races VALUES (32, 'Downtown Dunedin Half Marathon', '2019-01-19 00:00:00', 'Friends join us in this annual celebration of the Dunedin Half showcasing the most desirable town in the United States! This is a shady flat course along the completely flat and paved Pinellas County Fred Marquis Trail. You won''t find huge crowds and a congested course as each distance is limited to 200 participants and every registrant will receive the prized DUNEDIN Florida uniqe finisher''s medal.', 34, 'https://www.active.com/dunedin-fl/running/distance-running-races/the-downtown-dunedin-half-marathon-with-10k-and-5k-2019', true, '', 0, '2019-09-07 22:18:07', true);
INSERT INTO ournextrace.races VALUES (1, 'Zooma Florida Half Marathon', '2018-10-19 00:00:00', 'Zooma Florida was voted one of the top 20 half marathons AND top 20 for beautiful, on-course scenery', 2, 'http://zoomarun.com/race/florida/', true, '', 0, '2019-12-13 20:56:47.50327', true);
INSERT INTO ournextrace.races VALUES (33, 'Light Up the Night 5k', '2019-01-19 00:00:00', 'The mission of the Pasco Sheriff''s Office Light Up the Night Tampa Bay event is to bring awareness and education to all the surrounding areas of Tampa Bay about the perils of men, women, and children enslaved through the heinous acts of human trafficking', 35, 'https://lightupthenight.us/', true, '', 0, '2019-09-07 22:18:12', true);
INSERT INTO ournextrace.races VALUES (34, 'Running of the Bulls 5k', '2019-01-19 00:00:00', 'Be a part of this fantastic event and support a local high school! Runners and walkers of all paces are welcome! The Running of the Bulls 5k, an event put on by the Bloomingdale Band Boosters, will take place on the Bloomingdale High School campus, in Valrico, Florida. All proceeds go to Bloomingdale High School.', 36, 'https://fitniche.com/event/running-bulls-5k-2/', true, '', 0, '2019-09-07 22:18:16', true);
INSERT INTO ournextrace.races VALUES (35, 'DREAM Clearwater MLK Day 5K', '2019-01-19 00:00:00', 'On Monday, January 21, 2019, join us at 9 a.m. as we celebrate Martin Luther King Jr.  Day in this non competitive 5K walk/run recognizing The Clearwater MLK Jr. Neighborhood Center Coalition in Clearwater directed by Anthony Burney.', 37, 'https://www.active.com/clearwater-fl/running/distance-running-races/the-dream-clearwater-mlk-day-5k-on-memorial-causeway-2019', true, '', 0, '2019-09-07 22:18:23', true);
