INSERT INTO ournextrace.races VALUES (139, 'Mount Dora Bicycle Festival', '2019-10-03 00:00:00', 'Oct 3-Oct 6 - Mount Dora has been hosting bicyclists for more than 40 years and attracts a thousand bicyclists. Mount Dora is another charming small town. It keeps the visitors coming with a series of big festivals from fall to spring.', 0, 'https://www.mountdorabicyclefestival.com/', false, '', 0, '2019-10-02 00:02:18', false);
