INSERT INTO ournextrace.races (id, name, date, description, address_id, url, public, coupon_code, author_id, mod_date, active) VALUES (256, 'IRONMAN 70.3 Waco Waco, TX', '2020-10-18 00:00:00', 'Athletes looking for a beginner-friendly and fast course should look no further than Bicycle World Texas IRONMAN 70.3 Waco. This beautiful Central Texas race takes athletes on a down-river swim, a gentle bike course with a fast descent that leads to an iconic and scenic run course that ends with one of the most picturesque IRONMAN 70.3 finish lines in North America.', 226, 'https://www.ironman.com/im703-waco', true, NULL, 3, '2020-01-04 05:19:21.075698', true);
