INSERT INTO ournextrace.races (id, name, date, description, address_id, url, public, coupon_code, author_id, mod_date, active) VALUES (258, 'IRONMAN Arizona TEMPE, ARIZONA', '2020-11-22 00:00:00', 'The inaugural IRONMAN Arizona was held in 2005 and has since been the site of personal records for IRONMAN triathletes all over the world. Professional triathlete Lionel Sanders set an IRONMAN world best on this course back in 2016 and continues to deliver personal records each year. IRONMAN Arizona delivers a flat, fast, and spectator-friendly course that brings urban charms and desert vistas together.', 227, 'https://www.ironman.com/im-arizona', true, NULL, 3, '2020-01-04 05:23:06.606429', true);
